sys_script_include.script:
var SH_PPM = Class.create();
SH_PPM.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	addPPM: function(){
		var proj = new GlideRecord('pm_project');
		proj.initialize();
		proj.short_description =  //Project Name;
		proj.setValue('project_manager' , 'Mark Buckley'); //Project Manager;
		proj.setValue('primary portfolio' , ''); //Portfolio;
		proj.setValue('primary_program' , ''); //Program;
		proj.setValue('cmdb_ci',''); //Configuration Item;
		proj.state = -5; //State - pending
		proj.percent_complete = 0; //Percent Complete;
		proj.calculation = 'automatic'; //Calculation;
		proj.description =  'YOur Project Description'; //Description;

		//Dates
		proj.setValue('schedule','Project Management Schedule'); //Shedule
		proj.start_date = ''; //Planned Start Date;
		proj.end_date = ''; //Planned End Date;
		proj.duration = ''  ;//Planned Duration days and hours 00:00:00;

		var parentId = proj.insert();

		var error = proj.getLastErrorMessage();
		if(error == null){
			status = parentId;
		}else{
			status = error;
		}
		return status;	

	},

	type: 'SH_PPM'
});
